mapper.select:
select
from projects p where p.uuid in
#{uuid,jdbcType=VARCHAR}

select
from projects p where p.qualifier='TRK' and p.kee in
#{k,jdbcType=VARCHAR}

select
from projects p where p.qualifier='APP' and p.kee in
#{k,jdbcType=VARCHAR}

SELECT

FROM projects p inner join project_branches pb on pb.project_uuid = p.uuid
where pb.uuid=#{branchUuid,jdbcType=VARCHAR}

select
from projects p where p.qualifier='TRK' and p.organization_uuid in
#{orgUuid,jdbcType=VARCHAR}

mapper.update:
update projects set
tags = #{tagsString,jdbcType=VARCHAR},
updated_at = #{updatedAt,jdbcType=BIGINT}
where
uuid = #{uuid,jdbcType=VARCHAR}

update projects set
name = #{name,jdbcType=VARCHAR},
description = #{description,jdbcType=VARCHAR},
updated_at = #{updatedAt,jdbcType=BIGINT}
where
uuid = #{uuid,jdbcType=VARCHAR}

update projects set
private = #{isPrivate,jdbcType=BOOLEAN},
updated_at = #{updatedAt,jdbcType=BIGINT}
where
uuid = #{uuid,jdbcType=VARCHAR}

update projects set
ncloc = #{ncloc,jdbcType=BIGINT}
where
uuid = #{projectUuid,jdbcType=VARCHAR}

mapper.select:
(select 
from projects p
where p.uuid in

    #{uuid,jdbcType=VARCHAR}
  )
UNION
(select 
from portfolios p
where p.uuid in

    #{uuid,jdbcType=VARCHAR}
  )

(select 
from projects p
where p.kee in

    #{kee,jdbcType=VARCHAR}
  )
UNION
(select 
from portfolios p
where p.kee in

    #{kee,jdbcType=VARCHAR}
  )

(select 
from projects p

  where p.uuid = #{entityUuid,jdbcType=VARCHAR}
)
UNION
(select 
from portfolios p

  where p.uuid = #{entityUuid,jdbcType=VARCHAR}
)
